import React, {useState} from 'react';
import PopupButton from '../popupButton';
import TableCell from '@mui/material/TableCell';
import TableRow from '@mui/material/TableRow';
import TaskDetailPanel from './taskDetailPanel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const TaskItem = ({taskId, taskTitle, assignee, status, priority, deadline}) => {
    const [ass, setAss] = useState(assignee);
    const [sta, setSta] = useState(status);
    const [pri, setPri] = useState(priority);
    const [dea, setDea] = useState(deadline);

    return (
        <TableRow
            key={taskId}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
        >
            <TableCell component="th" scope="row">
                <PopupButton variant='text' buttonTitle={taskTitle} title={taskTitle}>
                    <TaskDetailPanel/>
                </PopupButton>
            </TableCell>
            <TableCell>
                <Select
                    value={ass}
                    label="Assignee"
                    onChange={e=>{setAss(e.target.value)}}
                >
                    <MenuItem value='Barry'>Barry</MenuItem>
                    <MenuItem value='Justin'>Justin</MenuItem>
                    <MenuItem value='Kai'>Kai</MenuItem>
                </Select>
            </TableCell>
            <TableCell>
                <Select
                    value={sta}
                    label="Status"
                    onChange={e=>{setSta(e.target.value)}}
                >
                    <MenuItem value='Not started yet'>Not started yet</MenuItem>
                    <MenuItem value='Working on it'>Working on it</MenuItem>
                    <MenuItem value='Completed'>Completed</MenuItem>
                </Select>
            </TableCell>
            <TableCell>
                <Select
                    value={pri}
                    label="Priority"
                    onChange={e=>{setPri(e.target.value)}}
                >
                    <MenuItem value='High'>High</MenuItem>
                    <MenuItem value='Medium'>Medium</MenuItem>
                    <MenuItem value='Low'>Low</MenuItem>
                </Select>
            </TableCell>
            <TableCell>
                <Select
                    value={dea}
                    label="Deadline"
                    onChange={e=>{setDea(e.target.value)}}
                >
                    <MenuItem value='Not started yet'>Not started yet</MenuItem>
                    <MenuItem value='Working on it'>Working on it</MenuItem>
                    <MenuItem value='Completed'>Completed</MenuItem>
                </Select>
            </TableCell>
        </TableRow>
    )
}

export default TaskItem;
