import React, {useState} from 'react';
import PopupButton from '../popupButton';
import TableCell from '@mui/material/TableCell';
import TableRow from '@mui/material/TableRow';
import TaskDetailPanel from './taskDetailPanel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';

const TaskItem = ({taskId, taskTitle, assignee, status, priority, deadline}) => {
    const [ass, setAss] = React.useState(assignee);

    return (
        <TableRow
            key={taskId}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
        >
            <TableCell component="th" scope="row">
                <PopupButton variant='text' buttonTitle={taskTitle} title={taskTitle}>
                    <TaskDetailPanel/>
                </PopupButton>
            </TableCell>
            <TableCell>
                <Select
                    value={ass}
                    label="Assignee"
                    onChange={e=>{setAss(e.target.value)}}
                >
                    <MenuItem value='Barry'>Barry</MenuItem>
                    <MenuItem value='Justin'>Justin</MenuItem>
                    <MenuItem value='Kai'>Kai</MenuItem>
                </Select>
            </TableCell>
            <TableCell>
                <Select
                    value={ass}
                    label="Assignee"
                    onChange={e=>{setAss(e.target.value)}}
                >
                    <MenuItem value='Barry'>Barry</MenuItem>
                    <MenuItem value='Justin'>Justin</MenuItem>
                    <MenuItem value='Kai'>Kai</MenuItem>
                </Select>
            </TableCell>
            <TableCell>{priority}</TableCell>
            <TableCell>{deadline}</TableCell>
        </TableRow>
    )
}

export default TaskItem;
